import {GameStart} from "./screens/GameStart";
import {Gameplay} from "./screens/Gameplay";
import react, {useReducer, useState} from "react";
import {Gameover} from "./screens/Gameover";
import {PropTypes} from "prop-types";
import { History } from "./components/History";
import {
    reducer, 
    changeRounds,
    changeCount,
    changeGameState,
    startTime,
    changeMemory, 
    changeStorage,
    resetMemory,
    initializer
} from "./logicContainers/appReducer";
import { createServer } from "./ServerAPI";
import { ServerContext } from "./ServerContext";
export function App(props) {
    const [state, dispatch] = useReducer(reducer, undefined, initializer);
    const game = {
        start: "start",
        play: "play",
        end: "end"
    };
    const setRounds = (rounds) => dispatch(changeRounds(rounds));
    const setGameState = (gameState) => dispatch(changeGameState(gameState));
    const setTime = () => dispatch(startTime());
    const setCount = (newCount) => dispatch(changeCount(newCount));
    const setMemory = (memoryObject) => dispatch(changeMemory(memoryObject));
    const clearMemory = () => dispatch(resetMemory());
    const setStorage = (memory) => dispatch(changeStorage(memory));

    return (
        <ServerContext.Provider value={createServer()} >
            <main>
                {state.gameState === game.start &&
                <GameStart state={state} 
                setRounds={setRounds} setGameState={setGameState} setTime={setTime}/> 

                }

                {state.gameState === game.play && 
                <>
                    <Gameplay state={state} setCount={setCount} setGameState={setGameState} setMemory={setMemory} setStorage={setStorage} />
                    <aside className="historyDisplay">
                        {state.memory.map((item) => (<History { ...item } />))}
                    </aside>
                </>
                }

                {state.gameState === game.end && <>
                <Gameover setRounds={setRounds} setTime={setTime} setGameState={setGameState} setCount={setCount} 
                clearMemory={clearMemory} setStorage={setStorage} state={state}/>

                </>}
            </main>
        </ServerContext.Provider>
    );

}

import {PropTypes} from "prop-types";

export function GameStart(props) {

const { state, setRounds, setGameState, setTime } = props;

const { rounds } = state;

    const handleClick = () => {
        setGameState("play");
        setTime(Date.now());
    };

    const handleChange=(e)=>{
        setRounds(Number.parseInt(e.target.value));
    }
    return (
        <div className="display">
            <p>Hi, this is James' math game, choose your parameters and get to calculating!</p>
            <input type="number" min="1" max="20" value={rounds} 
            onChange = {handleChange} autoFocus></input>
            <div>
                <button onClick={handleClick}>Start</button>
            </div>
        </div>
    );
}

GameStart.propTypes = {
    rounds: PropTypes.number.isRequired,
    changeMode: PropTypes.func.isRequired,
    changeRounds: PropTypes.func.isRequired
};

import {useState, useEffect, useReducer, useContext} from "react";
import {PropTypes} from "prop-types";
import { signsRef, evaluate } from "../utils/signs";
import useReducerWithMiddleware from "../hooks/useReducerWithMiddleware";
import {
    reducer, 
    changeSign,
    changeFirstNum,
    changeSecondNum,
    changeSkipUse,
    toggleRequest,
    createPostSucceeded,
    answerPostSucceeded,
    initializer
} from "../logicContainers/gamePlayReducer";
import { ServerContext } from "../ServerContext";

export function Gameplay(props) {
const { state, setCount, setGameState, setMemory } = props;

const { count, rounds, memory } = state;
const server = useContext(ServerContext);
const [subState, dispatchSub] = useReducer(reducer, undefined, initializer);
const time = Date.now();

const setSign = (sign) => dispatchSub(changeSign(sign));
const setFirstNum = (newFirstNum) => dispatchSub(changeFirstNum(newFirstNum));
const setSecondNum = (newSecondNum) => dispatchSub(changeSecondNum(newSecondNum));
const setSkipUse = (newSkipUse) => dispatchSub(changeSkipUse(newSkipUse));
const setRequestState = (requestState) => dispatchSub(toggleRequest());
const createSucceeded = (gameObject) => dispatchSub(createPostSucceeded(gameObject));
const answerSucceeded = (gameObject) => dispatchSub(answerPostSucceeded(gameObject));

useEffect(() => { 
    if (!subState.requestState.inFlight) {
        setRequestState({inFlight: true, error: null});
        server.createGame("mathemagician", rounds).then(
            (response) => {
                createSucceeded(response);
                setRequestState({inFlight: false, error: null});
            }
        ).catch( 
            (error) => {
                setRequestState({inFlight: false, error: error.message});
            }
        );
    }
}, []);
const handleSubmit = (e) => {
    e.preventDefault();
};

const skip = () => {
    setCount(count + 1);
    setRequestState({inFlight: true, error: null});
            server.answerGame(subState.id, "skip").then(
            (response) => {
                answerSucceeded(response);
                setRequestState({inFlight: false, error: null});
            }
        ).catch( 
            (error) => {
                setRequestState({inFlight: false, error: error.message});
            }
        );
};

const handleInputChange=(e) => {
    if (!subState.requestState.inFlight) {
        const answer = evaluate(subState.firstNum, subState.secondNum, subState.sign);
        const input = Number.parseInt(e.target.value);
        if (subState.CAL === input.toString().length) {
            // setMemory({
            //     firstNum: subState.firstNum,
            //     secondNum: subState.secondNum,
            //     answer: answer,
            //     sign: subState.sign,
            //     value: input,
            //     time: Date.now()-time,
            //     speed: Math.floor((Date.now()-time)/1000) < 3
            // });
            e.target.value = "";
            setRequestState({inFlight: true, error: null});
            server.answerGame(subState.id, input).then(
            (response) => {
                setMemory({
                    firstNum: subState.firstNum,
                    secondNum: subState.secondNum,
                    answer: response.move.correct,
                    sign: subState.sign,
                    value: input,
                    time: response.move.timeSpentMillis,
                    speed: Math.floor(response.move.timeSpentMillis/1000) < 3
                });
                if (response.game.nextExpression === null) {
                    setGameState("end");
                    return;
                }
                answerSucceeded(response);
                setRequestState({inFlight: false, error: null});
            }
            ).catch( 
                (error) => {
                    setRequestState({inFlight: false, error: error.message});
                }
            );
        }
    }
};

    return (
        <div className="display">
           { subState.error === null? 
            <p>{subState.error}</p> : subState.requestState.inFlight ? <p>Loading...</p> :
            <>
            <p>{subState.firstNum} {subState.sign} {subState.secondNum}</p>
            <form onSubmit={handleSubmit}>
                <input type="number" onChange={handleInputChange} autoFocus />
            </form>
           {subState.skipUse > 0 ? <button onClick={skip} >Skip</button> : null }
           </>}

        </div>
    );
}

Gameplay.propTypes = {
    rounds: PropTypes.number.isRequired,
    count: PropTypes.number.isRequired,
    setCount: PropTypes.func.isRequired,
    setGameState: PropTypes.func.isRequired
};

export const signsRef = ["+", "*", "-"];

export const evaluate = (firstNum, secondNum, sign) => {
    switch (sign) {
        case "+":
            return firstNum + secondNum;
        case "*":
            return firstNum * secondNum;
        case "/":
            return firstNum / secondNum;
        case "-":
            return firstNum - secondNum;
    }
};

import { useMemo, useReducer, useRef } from "react";

/**
 * Same as React's useReducer, but also executes middleware functions on every
 * action dispatch.
 *
 * A middleware is a powerful and flexible function that can
 * - dispatch arbitrary actions, e.g. at an interval
 * - inspect all the dispatched actions
 * - change an action before it reaches the reducer or next middleware
 * - stop an action from propagating altogether
 *
 * The middleware interface is designed to be exactly the same as Redux's
 * middlewares - the middleware function must have the following form:
 ```js
  ({getState}) => {
    return (next) =>
      return (action) => {
        next(action);
      }
    }
  }
 ```
 * A middleware function must call `next(action)` to propagate the action to
 * the final action dispatch and reducer, otherwise the action will be ignored.
 *
 * Middlewares are executed in reverse order.
 *
 * @param {Array<Function>} middlewares Middleware functions, executed in reverse order
 * @param {Function} reducer Passed to React's useReducer
 * @param {any} initOrInitialValue Passed to React's useReducer
 * @param {any} init Passed to React's useReducer
 * @return {Array} of [state, dispatch]
 */
const useReducerWithMiddleware = (
  middlewares,
  reducer,
  initOrInitialValue,
  init
) => {
  /*
   * In order to give access to last state to middleware functions, need to
   * provide them a value which itself doesn't change to avoid constantly
   * re-defining the middlewares. `useRef` provides just the "box" which we can
   * mutate for this purpose.
   */
  const lastStateRef = useRef();

  // `useMemo` will be discussed in next lecture
  const refUpdatingReducer = useMemo(
    () => (prevState, action) => {
      const newState = reducer(prevState, action);
      lastStateRef.current = newState;
      return newState;
    },
    [lastStateRef]
  );

  const [state, dispatch] = useReducer(
    refUpdatingReducer,
    initOrInitialValue,
    init
  );

  const dispatchWithMiddlewares = useMemo(() => {
    const middlewareInit = {
      getStateBefore: () => lastStateRef.current,
    };

    return middlewares
      .concat(dispatch)
      .reverse()
      .reduce((acc, middleware) => {
        /*
         * Initialize the middleware with means to get state and the next
         * middleware/final dispatch.
         */
        return middleware(middlewareInit)(acc);
      });
  }, [middlewares, dispatch, lastStateRef]);
  return [state, dispatchWithMiddlewares];
};

export default useReducerWithMiddleware;

export const toggleRequest = (requestState) => ({
    type: "toggleRequest",
    payload: {
        inFlight: false,
        error: null
    }
});

export const createPostSucceeded = (gameObject) => ({
    type: "createPostSucceeded",
    payload: gameObject
});

export const answerPostSucceeded = (gameObject) => ({
    type: "answerPostSucceeded",
    payload: gameObject
});

export const changeSign = (newSign) => ({
    type: "changeSign",
    payload: newSign
});

export const changeFirstNum = (newFirstNum) => ({
    type: "changeFirstNum",
    payload: newFirstNum
});

export const changeSecondNum = (newSecondNum) => ({
    type: "changeSecondNum",
    payload: newSecondNum
});

export const changeSkipUse = (newSkipuse) => ({
    type: "changeSkipUse",
    payload: newSkipuse
});

export const initializer = () => ({
    id: null,
    sign: null,
    firstNum: null,
    secondNum: null,
    skipUse: 0,
    CAL: 0,
    requestState: {
        inFlight: false,
        error: null
    }
});

const setRequestState = (state, requestState) => {
    console.log("James the flyer");
   return {...state,
    requestState: requestState};
};

const createGame = (state, gameObject) => ({
    ...state,
    id: gameObject.id,
    sign: gameObject.nextExpression.operator,
    firstNum: gameObject.nextExpression.lhs,
    secondNum: gameObject.nextExpression.rhs,
    skipUse: gameObject.skipsRemaining,
    CAL: gameObject.nextExpression.correctAnswerLength
});
const answerGame = (state, gameObject) => ({
    ...state,
    firstNum: gameObject.game.nextExpression.lhs,
    secondNum: gameObject.game.nextExpression.rhs,
    sign: gameObject.game.nextExpression.operator,
    skipUse: gameObject.game.skipsRemaining,
    CAL: gameObject.game.nextExpression.correctAnswerLength
});

const setSign = (state, newSign) => ({
    ...state,
    sign: newSign
});

const setFirstNum = (state, newFirstNum) => ({
    ...state,
    firstNum: newFirstNum
});

const setSecondNum = (state, newSecondNum) => ({
    ...state,
    secondNum: newSecondNum
});

const setSkipuse = (state, newSkipuse) => ({
    ...state,
    skipUse: newSkipuse
});

export const reducer = (state, action) => {
    switch (action.type) {
        case "changeSign": 
            return setSign(state, action.payload);
        case "changeFirstNum":
            return setFirstNum(state, action.payload);
        case "changeSecondNum":
            return setSecondNum(state, action.payload);
        case "changeSkipUse":
            return setSkipuse(state, action.payload);
        case "createPostSucceeded":
            return createGame(state, action.payload);
        case "toggleRequest":
            return setRequestState(state, action.payload);
        case "answerPostSucceeded":
            return answerGame(state, action.payload);
        default:
            throw new Error("Invalid gameplay reducer usage");
    }
};

import {PropTypes} from "prop-types";
import { useEffect } from "react";
import { History } from "../components/History";

export function Gameover(props) {

    const { setRounds, setTime, setGameState, setCount, clearMemory, setStorage, state } = props;
    const { time, count, memory, storage } = state;
    const timeSpent = Date.now()-time;
    let inputVal=count; 
    const keyArray=Object.keys(storage);
    useEffect(() =>{
        setStorage(memory);
    },[memory]);
    const handleClick = (e) => {
        setCount(1);
        setRounds(Number.parseInt(inputVal));
        setTime(Date.now());
        setGameState("play");
        clearMemory();
    };

    const handleInputChange = (e) => {
        inputVal = e.target.value; 
    };
    return (
        <div className="display">
            <p>You spent {timeSpent} milliseconds playing </p>
            <input type="number" defaultValue={count} 
            id="roundChanger" min='1' max='20' onChange={handleInputChange} autoFocus></input>
            <button onClick={handleClick} autoFocus>Play again?</button>
            {keyArray.map((key) => {
                    return (
                        <div>
                            <p>{key}</p>
                            {storage[key].map( (item)=> (<History { ...item } />))}
                        </div>
                    );
                })}
        </div>
    );
}


Gameover.propTypes = {
    reset: PropTypes.func.isRequired,
    time: PropTypes.number.isRequired,
    count: PropTypes.number.isRequired,

};

import { StyledHistory } from "../styledComponents/StyledHistory";

export const History = (props) => {
    const { firstNum, secondNum, sign, value, time, answer, speed } = props;

    return (
        <StyledHistory value={value} answer={answer} speed={speed}>
            <p>{firstNum} {sign} {secondNum} [{time}]</p>
            <p>{value}</p>
        </StyledHistory>
    );

};

import styled from "styled-components";

export const StyledHistory = styled.li`
    list-style-type: none;
    margin: 1.5rem 1.5rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 1.5rem;
    border-radius: 0.8rem;
    color: ${(props) => props.speed ? "green" : "orange"};
    color: ${(props) => !props.answer? "red" : ""};
    background-color: rgba(222, 165, 78, 0.501);
`;

let count = 0;
export const changeRounds = (newRounds) => ({
    type: "changeRounds",
    payload: newRounds
});

export const changeCount = (newCount) => ({
    type: "changeCount",
    payload: newCount
});

export const changeGameState = (newGameState) => ({
    type: "changeGameState",
    payload: newGameState
});

export const startTime = () => ({
    type: "startTime"
});

export const changeMemory = (memoryObject) => ({
    type: "changeMemory",
    payload: memoryObject
});

export const changeStorage = (newArray) => ({
    type: "changeStorage",
    payload: newArray
});

export const resetMemory = () => ({
    type: "resetMemory"
});

export const initializer = () => ({
    rounds: 3,
    count: 1,
    gameState: "start",
    time: null,
    memory: [],
    storage: {}
});

const setRounds = (state, newRounds) => ({
    ...state, 
    rounds: newRounds
});

const setCount = (state, newCount) => {
    return {
        ...state,
        count: newCount
    };
};

const setGameState = (state, newGameState) => ({
    ...state,
    gameState: newGameState
});

const setTime = (state) => ({
    ...state,
    time: Date.now()
});

const updateMemory = (state, memoryObject) => ({
    ...state,
    memory: state.memory.concat([{...memoryObject}])
});

const clearMemory = (state) => ({
    ...state,
    memory: []
});

const updateStorage = (state, newArray) => {
    count++;
    const key = `game${count}`;
    return {
    ...state,
    storage: {
        ...state.storage,
        [key]: newArray
    }
    };
};

export const reducer = (state, action) => {
    switch (action.type) {
        case "changeRounds":
            return setRounds(state, action.payload);
        case "changeCount":
            return setCount(state, action.payload);
        case "changeGameState":
            return setGameState(state, action.payload);
        case "startTime":
            return setTime(state);
        case "changeMemory": 
            return updateMemory(state, action.payload);
        case "changeStorage":
            return updateStorage(state, action.payload);
        case "resetMemory":
            return clearMemory(state);
        default:
            throw new Error("Invalid App reducer Usage");
    }
};

const SERVER_ADDRESS = "http://localhost:8081";

const parse = async (fetchPromise) => {
    const response = await fetchPromise;
    if (response.ok) {
      return await response.json();
    } else {
      throw new Error((await response.json()).error);
    }
  };


export const createGame = (type, rounds) => {
    return parse(
        fetch(SERVER_ADDRESS + "/games", {
            method: "POST",
            headers: {
                "content-type": "application/json"
            },
            body: JSON.stringify({ type: type, rounds: rounds})
        })
    );
};

export const answerGame = (id, guess) => {
    return parse(
        fetch(SERVER_ADDRESS + `/games/${id}/moves`, {
            method: "POST",
            headers: {
                "content-type": "application/json"
            },
            body: JSON.stringify({
                guess: guess
            })
        })
    );
};

export const createServer = () => {
    return {
        createGame: (type, rounds) => createGame(type, rounds),
        answerGame: (id, guess) => answerGame(id, guess)
    };
};
